FROM v_SmsPackage as app
JOIN v_Program as pro on pro.PackageID = app.PkgID
WHERE pro.ProgramName <> '*' and app.[Name] NOT LIKE '%User State%' and app.[Name] NOT LIKE '%Configuration Manager%'


"@

